                    'please_log_in',
                ]
            );
            $this->settings_protected = array_flip(
                [
                    'item_purchase_code',
                    'rml_slug',
                    'take_over_rml',
                    'fb_app_id',
                    'fb_app_secret',
                    'fb_authed',
                    'fb_overview_slug',
                    'fli_api_key',
                    'fli_added',
                    'flickr_collections_slug',
                    'ig_client_id',
                    'ig_client_secret',
                    'ig_access_token',
