            // If this JIG is based on a saved gallery
            if (!empty($atts['gallery'])) {
                $pairs = array_merge($pairs, $gallery_atts);
            }

            extract(
                shortcode_atts(
                    $pairs,
                    $atts,
                    ('' === $this->settings['shortcode_alias'] ?
                        'justified_image_grid' :
                        $this->settings['shortcode_alias']
                    )
                )
            );

            if ('yes' === $for_xml_sitemap) {
                $post = get_post($id);
            }

